package javaproject;

public class Typecasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Convertion of datatype into another
		
		//implicitly casting
		//explicitly casting 
		
		int a= 10;
		short b= (short)a;
		System.out.println(b);
		
		double c = 10.2d;
		float d = (float)c;
		System.out.println(c);
		
		

	}

}
